INSERT INTO genres(name, description) VALUES('Thriller', 'The ‘Thriller’ movie genre is characterized by its focus on suspense, tension, and excitement, often involving high-stakes scenarios and unexpected plot twists that keep the audience on the edge of their seats.');
INSERT INTO genres(name, description) VALUES('Drama', 'Drama is a film genre that focuses on character development and emotional narratives, often exploring complex human relationships and societal issues.');
INSERT INTO genres(name, description) VALUES('Superhero film', 'A superhero film is a film that focuses on superheroes and their actions. Superheroes are individuals who usually possess superhuman abilities');
