INSERT INTO movies(title, description, genre, director, image) VALUES('Silence of the Lambs', 'A young FBI cadet must receive the help of an incarcerated and manipulative cannibal killer to help catch another serial killer.', 1, 2, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('The Godfather', 'The Godfather is a gripping crime drama that chronicles the powerful Corleone family’s rise and struggles within the mafia underworld, blending themes of loyalty, power, and betrayal.', 4, 1, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('The Shawshank Redemption', 'The film tells the story of banker Andy Dufresne (Tim Robbins), who is sentenced to life in Shawshank State Penitentiary for the murders of his wife and her lover, despite his claims of innocence.', 2, 3, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('The Dark Knight', 'The plot follows the vigilante Batman, police lieutenant James Gordon, and district attorney Harvey Dent, who form an alliance to dismantle organized crime in Gotham City', 3, 4, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('Pulp Fiction', 'The film tells four intertwining tales of crime and violence in Los Angeles, California', 4, 5, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('Forrest Gump', 'The film follows the life of an Alabama man named Forrest Gump and his experiences in the 20th-century United States.', 5, 6, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('Memento', 'The film follows Leonard Shelby (Pearce), a man who suffers from anterograde amnesia—resulting in short-term memory loss and the inability to form new memories—who uses an elaborate system of photographs, handwritten notes, and tattoos in an attempt to uncover the perpetrator who killed his wife', 1, 4, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('Fight Club', 'The protagonist forms a fight club with a soap salesman, and becomes embroiled in a relationship with an impoverished but beguilingly attractive woman.', 1, 7, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('The Matrix', 'The movie depicts a dystopian future in which humanity is unknowingly trapped inside the Matrix, a simulated reality that intelligent machines have created to distract humans while using their bodies as an energy source.', 1, 8, 'imagelink');
INSERT INTO movies(title, description, genre, director, image) VALUES('The Lord of the Rings: The Return of the King', 'Continuing the plot of the previous film, Frodo, Sam and, Gollum make their final way toward Mount Doom to destroy the One Ring, unaware of Gollum''s true intentions, while Merry, Pippin, Gandalf, Aragorn, Legolas, Gimli and, their allies join forces against Sauron and his legions from Mordor.', 6, 9, 'imagelink');
